import React from 'react';
import PropTypes from 'prop-types';
import ReactDOM from 'react-dom';
import { withRouter } from 'react-router';
import './MenuItem.css'

class MenuItem extends React.Component {
  constructor(props){
    super(props);
  }
  static propTypes = {
    match: PropTypes.object.isRequired,
    location: PropTypes.object.isRequired,
    history: PropTypes.object.isRequired
  }
  render(){

    // const view=null;
    var view;
    const { match, location, history } = this.props

    const headerColor = location.pathname === 'contactUs' ? { background: 'white'} : { background: 'blue' }


    if (this.props.item.type === 1)

        {view= (<li className="nav-item px-1">
          <div style={headerColor}>You are now at {location.pathname}</div>
          <a className="nav-link txtHeader " href={this.props.item.url}>{this.props.item.title}</a>
        </li>)}
    else if (this.props.item.type === 2)
    {
      view=(<li class="nav-item ml-auto ">
      <a className="nav-link txtHeader " href={this.props.item.url}>{this.props.item.title}<i className={this.props.item.icon}></i></a>
      </li>)
    }
    else
    {
      view=(<li class="list-inline-item">
      <a className="btn-floating btn-fb mx-1" href={this.props.item.url}>
      <i className={this.props.item.icon}></i></a>
      </li>
      )
    }
    return( 

      view
     );
  }
}



