import React, { Component } from 'react'
import './infoArea.css'

class InfoArea extends Component {
    constructor(props){
        super(props);
      }
    render() {
        return (
            <div className="infoArea row ml-1">
                <div className="col-12 col-lg-6 p-0 ml-5">
                    <img src={this.props.item.img} class="img-fluid" alt="Responsive image"/>
                </div>

                <div className="col-12 col-lg-5 infoBox mt-4 pt-5 pl-2 pr-2">
                    <div className="horiz-line mb-4" data-aos="fade-in"/>
                    <p className="mt-3">{this.props.item.info}</p>
                    <div className="horiz-line mt-4"/>
                </div>
            </div>
        )
    }
}
export default InfoArea;
